import React from 'react';
import ChatBot from 'react-simple-chatbot';
import './App.css'; // Import your custom CSS file here
import user1 from './img/user1.png'
import user2 from './img/user2.png'
function App() {
  // Custom style for the chatbot
  const customStyle = {
    // Change the background color
    background: 'red',
    // Change the font color
    fontFamily: 'Arial, sans-serif',
  };

  // Custom steps for the chatbot
  const steps = [
    {
      id: '1',
      message: 'What is your name?',
      trigger: '2',
    },
    {
      id: '2',
      user: true,
      trigger: '3',
    },
    {
      id: '3',
      message: 'Hi {previousValue}, nice to meet you!',
      trigger: '4',
    },
    {
      id: '4',
      message: 'What number am I thinking?',
      trigger: '5',
    },
    {
      id: '5',
      options: [
        { value: 1, label: 'Number 1', trigger: '7' },
        { value: 2, label: 'Number 2', trigger: '6' },
        { value: 3, label: 'Number 3', trigger: '6' },
      ],
    },
    {
      id: '6',
      message: 'Wrong answer, try again.',
      trigger: '5',
    },
    {
      id: '7',
      message: 'Awesome! You are a telepath!',
      end: true,
    },
  ];

  return (
    <div>
      <div style={customStyle}>
        <ChatBot
          steps={steps}
          floating={true}
          headerTitle="Comapany Name" // Add a custom title
          botAvatar={user1} // Add a custom bot avatar
          userAvatar={user2} // Add a custom user avatar
          botDelay={1000} // Add a delay for bot responses
        />
      </div>
    </div>
  );
}

export default App;
